import React from "react";

class StatefulGreeting extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      instruction: "Hello, I am a Stateful Component!", 
      buttonText: "Exit"
    }
  }

  render(){
    return (
      <div>
        <h1>
          {this.state.instruction} {this.props.greeting}
        </h1>
        <button onClick={() => this}> {this.state.buttonText}</button>
      </div>
    ); 
  }
}

export default StatefulGreeting;
