import React from "react";

class StatefulGreeting extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      instruction: "Hello!", 
      buttonText: "Exit"
    }
  }

  render(){
    return(
      <div>
        <h1>{this.state.introduction} {this.props.greeting}</h1>
        <button>{this.state.button}</button>
      </div>
    ) 
  }
}

export default StatefulGreeting;
